var graphicLine = function (ctx, data) {
    var myChart = new Chart(ctx, {
        type: 'line',
        data: {
            labels: [
                "Janeiro",
                "Fevereiro",
                "Março",
                "Abril",
                "Maio",
                "Junho",
                "Julho",
                "Agosto",
                "Setembro",
                "Outubro",
                "Novembro",
                "Dezembro"
            ],
            datasets: [{
                label: 'Métricas do Ano',
                data: data,
                borderColor: [
                    'rgba(54, 162, 235, 0.2)'
                ]
            }]
        },
        options: {
            scales: {
                yAxes: [{
                    stacked: true
                }]
            }
        }
    });

    return myChart;
};

